static int convertCharStreamToHex(const char input[], uint8_t *output) {
	int j = 0;
	for (int i = 0; i < strlen(input); i += 2) {
		output[j] = (isalpha(input[i]) ? input[i] - 87 : input[i] - 48) * 16 + (isalpha(input[i + 1]) ? input[i + 1] - 87 : input[i + 1] - 48);
		j++;
	}
	return 1;
}

